mapper.select:
select
id, address_id, name, subtitle, url, link, icon_url, picture_url, parent_id, model_type, model_id, recommend_plate_id,
pc_page, mobile_page, pc_desc, mobile_desc, tip
from part


        and id = #{id}


        and address_id = #{addressId}


        and name = #{name}


        and subtitle = #{subtitle}


        and url = #{url}


        and link = #{link}


        and icon_url = #{iconUrl}


        and picture_url = #{pictureUrl}


        and parent_id = #{parentId}


        and model_type = #{modelType}


        and model_id = #{modelId}


        and recommend_plate_id = #{recommendPlateId}


        and pc_page = #{pcPage}


        and mobile_page = #{mobilePage}


        and pc_desc = #{pcDesc}


        and mobile_desc = #{mobileDesc}


        and tip = #{tip}

select count(1)
from part


        and id = #{id}


        and address_id = #{addressId}


        and name = #{name}


        and subtitle = #{subtitle}


        and url = #{url}


        and link = #{link}


        and icon_url = #{iconUrl}


        and picture_url = #{pictureUrl}


        and parent_id = #{parentId}


        and model_type = #{modelType}


        and model_id = #{modelId}


        and recommend_plate_id = #{recommendPlateId}


        and pc_page = #{pcPage}


        and mobile_page = #{mobilePage}


        and pc_desc = #{pcDesc}


        and mobile_desc = #{mobileDesc}


        and tip = #{tip}
